import React from 'react'
import {
    Magnifier,
    GlassMagnifier,
    SideBySideMagnifier,
    PictureInPictureMagnifier,
    MOUSE_ACTIVATION,
    TOUCH_ACTIVATION,
    MOVEMENT_METHOD
  } from "react-image-magnifiers";



export default function ProjectsItem(props) {

const {image, webSiteName} =props;



    return (

            <div className="projects" >
                <p id="MyImage"> {webSiteName} </p>

                <div className="project-imgS">

                    {/* <img src={`${window.location.origin}/Images/${image}`} alt=""   /> */}

                    <Magnifier
                        imageSrc={`${window.location.origin}/Images/${image}`}
                        imageAlt="Example"
                        // movementMethod={MOVEMENT_METHOD.hover}
                        // largeImageSrc="./large-image.jpg" // Optional
                        mouseActivation={MOUSE_ACTIVATION.D} // Optional
                        touchActivation={TOUCH_ACTIVATION.DOUBLE_TAP} // Optional
                        />
                </div>

            </div>
    )
}
