import React, { useState } from 'react'
import './Navigation.css'
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import MenuIcon from '@material-ui/icons/Menu';
import { SwipeableDrawer } from '@material-ui/core';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
// import { Link } from 'react-router-dom';
import { HashLink as Link } from 'react-router-hash-link';

export default function Navigation() {


    const [OpenMenu, setOpenMenu] = useState()

    const sideNavMenuOpen=()=>{
        setOpenMenu(true)
    }
    

    const sideNavMenuClose=()=>{

        document.getElementById("Close-button").classList.add("ghuratis");
        setTimeout(()=>{
            setOpenMenu(false);
        },800)

    }

    window.addEventListener('scroll', ()=>{
        let scroll= window.scrollY;
        let width= window.innerWidth;
        if(width > 850){
            var fixedNav= 600
        }
        else if(width > 450  ){
            var fixe
        }
        



        if(scroll > fixedNav){
        document.getElementById('Navigation-main').classList.add("Navigation-main")
    }
        else{
        document.getElementById('Navigation-main').classList.remove("Navigation-main")
        }
    })


    return (
        <div id="Navigation-main">
            <Grid className="Navigation-Parent"  justify="flex-start"  container  >
                <Button className="nav-button" variant="outlined" color="primary" item> <Link to="#home"> Home </Link> </Button>
                <Button className="nav-button" variant="outlined" color="primary" item> <Link to="#about"> About  </Link>  </Button>
                <Button className="nav-button" variant="outlined" color="primary" item> <Link to="#skills"> Skills  </Link>  </Button>
                <Button className="nav-button" variant="outlined" color="primary" item> <Link to="#projects"> Projects  </Link>  </Button>
                <Button className="nav-button" variant="outlined" color="primary" item> <Link to="#contact">  Contact </Link>  </Button>

                <Button className="nav-menu" onClick={sideNavMenuOpen} > <MenuIcon color="primary" /> </Button> 
            </Grid>

            <div>
                
                <SwipeableDrawer open={OpenMenu}  style={{height:"250px"}} swipeAreaWidth="20" className="sideNavMenu bg-dark">

                <div className="sideMenuCloseIcon">
                    <Button onClick={sideNavMenuClose} id="Close-button" > <ArrowBackIosIcon fontSize="large" /> </Button>
                </div>

                <div className="language">
                    <div className="btn"> BN </div> <div className="btn"> EN </div>
                </div>


                <div className="sideNavButton"> 
                <Button> <Link to="#home">  Home  </Link>  </Button>
                <Button> <Link to="#about">  About  </Link>  </Button>
                <Button> <Link to="#skills">  Skills  </Link>  </Button>
                <Button> <Link to="#projects">  Projects  </Link>  </Button>
                <Button> <Link to="#contact">  Contact  </Link>  </Button>
                <div class="SideNav-line"> 
                    <div className="putupPutu"> </div>
                    <div className="putupPutu"> </div>
                    <div className="putupPutu"> </div>
                    <div className="putupPutu"> </div>
                    <div className="putupPutu"> </div>
                </div>
                </div>
                

                </SwipeableDrawer>

            </div>
        </div>
    )
}
