import './App.css';
import Cover from './Component/Cover/Cover';
import Navigation from './Component/Navigation/Navigation';
import AboutSection from './Component/About/AboutSection';
import MySkills from './Component/MySkills/MySkills';
import Projects from './Component/Projects/Projects';
import ClientsComment from './Component/ClientsComment/ClientsComment';
import Services from './Component/Services/Services'
import Contact from './Component/Contact/Contact'
import React, { useState } from 'react'
import './Component/Navigation/Navigation.css'
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import MenuIcon from '@material-ui/icons/Menu';
import { SwipeableDrawer } from '@material-ui/core';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';





function App() {

  const [OpenMenu, setOpenMenu] = useState();

  const sideNavMenuOpen=()=>{
      setOpenMenu(true)
  }


  const sideNavMenuClose=()=>{

      document.getElementById("Close-button").classList.add("ghuratis");
      setTimeout(()=>{
          setOpenMenu(false);
      },800)

  }








  return (
    <div className="App">

<div>
            <Grid className="Navigation-Parent"  justify="flex-start"  container  >
                <Button className="nav-button" variant="outlined" color="primary" item>  Home   </Button>
                <Button className="nav-button" variant="outlined" color="primary" item>  About   </Button>
                <Button className="nav-button" variant="outlined" color="primary" item>  Skills   </Button>
                <Button className="nav-button" variant="outlined" color="primary" item>  Projects   </Button>
                <Button className="nav-button" variant="outlined" color="primary" item> <a href="#contact"> Contact  </a> </Button>

                <Button className="nav-menu" onClick={sideNavMenuOpen} > <MenuIcon color="primary" /> </Button> 
            </Grid>

            <div>

                <SwipeableDrawer open={OpenMenu}  style={{height:"250px"}} swipeAreaWidth="20" className="sideNavMenu bg-dark">

                <div className="sideMenuCloseIcon">
                    <Button onClick={sideNavMenuClose} id="Close-button" > <ArrowBackIosIcon fontSize="large" /> </Button>
                </div>

                <div className="language">
                    <div className="btn"> BN </div> <div className="btn"> EN </div>
                </div>


                <div className="sideNavButton"> 
                <Button> Home </Button>
                <Button> About </Button>
                <Button> Skills </Button>
                <Button> Projects </Button>
                <Button> Contact </Button>
                <div class="SideNav-line"> 
                    <div className="putupPutu"> </div>
                    <div className="putupPutu"> </div>
                    <div className="putupPutu"> </div>
                    <div className="putupPutu"> </div>
                    <div className="putupPutu"> </div>
                </div>
                </div>


                </SwipeableDrawer>

            </div>
        </div>


























      <Cover  id="top"/>
      {/* <Navigation/> */}
      <AboutSection/>
      <MySkills />
      <Projects />
      <ClientsComment />
      <Services/>
      <Contact id="contact"/>

<br/><br/><br/><br/><br/><br/><br/><br/><br/>
<br/><br/><br/><br/><br/><br/><br/><br/><br/>
<br/><br/><br/><br/><br/><br/><br/><br/><br/>

    <button>  <a href="#top"> Top ^  </a> </button>



    </div>
  );
}

export default App;
